            activInterval.Font.Bold = true;

            cells[startRow - recCounter, 0, startRow, 9].Borders.LineStyle = LineStyle.Continous;

            SaveAsXls(workbook, @"\Отчеты\Командировки\", filename);
            RunXls(@"\Отчеты\Командировки\", filename);

            return true;
        
        }

