                    rowPeriodData.RemoveAll(bdsm => bdsm.PeriodAccountNum.Contains("313"));
                    rowPeriodData.Add(new CashPaymentsPeriodBalanceDTO
                    {
                        FlagDebitCredit = 1,
                        PeriodAccountId = 9999,
                        PeriodAccountNum = "313",
                        PeriodPayment = sumOf313AccountsDebit,
