            int? currentMonth;
            int? currentQuarter;
            int countMonth;
            int countQuarter;

            for (int y = 0; y < numYear.Count(); y++)
            {

                List<BankPaymentsReportTrialBalanceQuarterDTO> currList = rowData.Where(x => x.Pr == 0 && x.YearNumber == numYear[y].YearNumber).ToList();
