                activIntervalSum.Font.Bold = true;
                activIntervalSum.Font.Size = 12;
                activIntervalSum.Interior.Color = Color.LightBlue;
                string rangeSUM = "";
                for (var i = DateCol + 1; i <= currentCreditCol; i++)
                {
