            cells[vsS[startHeaderPosition] + (startPosition + 1) + ":" + vsS[startHeaderPosition] + (startPosition + 2)].Merge();
            cells[startPosition - 1, startHeaderPosition - 1].Value = "Сальдо на початок періоду";

