        public static string NumberToYear(int number)
        {
            string year = "";
            switch (number)
            {
                case 1:
                    year = "рік";
                    break;
                case 2:
                case 3:
                case 4:
                    year = "роки";
                    break;
                case 5:
                case 6:
                case 7:
                case 8:
                case 9:
                case 10:
                case 11:
                case 12:
                case 13:
                case 14:
                case 15:
                case 16:
                case 17:
                case 18:
                case 19:
                case 20:
                    year = "років";
                    break;
                default:
                    break;

            }
            return year;
        }


